  override fun click() = println("I was clicked")
}
class Button : Clickable, Focusable {
  override fun click() = println("I was clicked")
  override fun showOff() {
      // 使用尖括号加上父类型名字的super表明调用哪个父类的方法
      // 在java中可以把基类的名字放在super关键字前面，如: Clickable.super.showOff()
      super.showOff()
      super.showOff()
